package operators1;

public class Arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c = 30;
		System.out.println("Addition of three numbers is "+ (a + b + c));
		System.out.println("Subtraction of three numbers is "+ (a - b - c));
		System.out.println("Multiplication of three numbers is "+ (a * b * c));
		System.out.println("Division of three numbers is "+ (a / b / c));
		System.out.println("Modulus of three numbers is "+ (a % b % c));

	}

}
