package operators1;

public class Comparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int p = 100;
		int q = 50;
		
		// if p is equal to q then it returns true
		System.out.println("p == q = " + (p == q) );
		// if p is not equal to q then it returns true
	    System.out.println("p != q = " + (p != q) );
		// if p is greater than q then it returns true
	    System.out.println("p > q = " + (p > q) );
		// if p is less than q then it returns true
	    System.out.println("p < q = " + (p < q) );
		// if p is greater than or equal to q then it returns true
	    System.out.println("p >= q = " + (p >= q) );
		// if p is less than or equal to q then it returns true
	    System.out.println("p <= q = " + (p <= q) );
	}

}
